mapper.select:
SELECT id, session_id AS sessionId, product_id AS productId, quantity
FROM cart_item
WHERE session_id = #{sessionId}

SELECT id, session_id AS sessionId, product_id AS productId, quantity
FROM cart_item
WHERE session_id = #{sessionId} AND product_id = #{productId}

mapper.update:
UPDATE cart_item
SET quantity = #{quantity}
WHERE id = #{id}
